try {
  var form = kony.application.getCurrentForm();
  var ctrl = form && (form.controller || form.__controller);
  if (!ctrl) {
    kony.ui.Alert({ message: "No controller or no form found!", alertType: constants.ALERT_TYPE_INFO }, {});
    return;
  }
  if (typeof ctrl.CompletedTask === "function") {
    ctrl.CompletedTask(context);
    return;
  }
  kony.ui.Alert({ message: "CompletedTask NOT FOUND IN THE CONTROLLER ", alertType: constants.ALERT_TYPE_INFO }, {});
} catch (e) {
  kony.ui.Alert({ message: "ERR: " + e, alertType: constants.ALERT_TYPE_INFO }, {});
}
