try {

   var frmHomeCtrl = require("frmHomeController");

   var idx = -1;
   if (context && typeof context.rowIndex === "number") {
     idx = context.rowIndex;
   } else if (context && context.widgetInfo && context.widgetInfo.selectedRowIndex) {

     idx = context.widgetInfo.selectedRowIndex[1];
   }

   if (frmHomeCtrl && typeof frmHomeCtrl.deleteTask === "function") {
     frmHomeCtrl.deleteTask({ rowIndex: idx });
     return;
   }
   kony.ui.Alert(
     { message: "frmHomeController.deleteTask", alertType: constants.ALERT_TYPE_INFO },
     {}
   );
 } catch (e) {
   kony.ui.Alert(
     { message: "ERROR in AS_Button_Delete: " + e, alertType: constants.ALERT_TYPE_INFO },
     {}
   );
 }
