try {
        var f = kony.application.getCurrentForm();

        if (f && f.controller){
          var keys = Object.keys(f.controller);
          kony.ui.Alert({
            message: "Controller funcs : = " + keys.join(", ") ,
            alertType: constants.ALERT_TYPE_INFO
        }, {});
        }else {
          kony.ui.Alert({
            message: "NO  controller FOUND ",
            alertType: constants.ALERT_TYPE_INFO
        }, {});
        }



    } catch (e) {
        kony.ui.Alert({
            message: "ERR" + e,
            alertType: constants.ALERT_TYPE_INFO
        }, {});

    }
