try {
  var form = kony.application.getCurrentForm();
  // نحاول نلقى الكنترولر بأي طريقة
  var ctrl = form && (form.controller || form.__controller);
  if (!ctrl) {
    kony.ui.Alert({ message: "No controller or no form found!" + form.id + "\ncontroller" + ctrl , 
                   alertType: constants.ALERT_TYPE_INFO }, {});
    return;
  }
  if (typeof ctrl.deleteTask === "function") {
    ctrl.deleteTask(context);      
    return;
  }
  kony.ui.Alert({ message: "deleteTask NOT FOUND IN THE CONTROLLER !", 
                 alertType: constants.ALERT_TYPE_INFO }, {});
} catch (e) {
  kony.ui.Alert({ message: "ERR: " + e, alertType: constants.ALERT_TYPE_INFO }, {});
}
