try {
 var f = kony.application.getCurrentForm();
 if (f && f.controller) {
   var controllerName = f._konyControllerName || "UNKNOWN";
   var methods = Object.keys(f.controller);
   kony.ui.Alert({
     message:
       "Form = " + (f.id || "??") +
       "\nController = " + controllerName +
       "\n\nFunctions:\n" + methods.join("\n"),
     alertType: constants.ALERT_TYPE_INFO
   }, {});
 } else {
   kony.ui.Alert({
     message: "No controller or no form found!",
     alertType: constants.ALERT_TYPE_INFO
   }, {});
 }
} catch (e) {
 kony.ui.Alert({
   message: "ERR while listing functions: " + e,
   alertType: constants.ALERT_TYPE_INFO
 }, {});
}
