try {
    var form = kony.application.getCurrentForm();
    if (form && form.controller) {
    var funcs = [];
    for (var k in form.controller) {
      if (typeof form.controller[k] === "function") {

        funcs.push(k);
      }
   }
    kony.ui.Alert({
     message: "Functions in controller: " + funcs.join(", "),
     alertType: constants.ALERT_TYPE_INFO
      }, {});
     } else {
     kony.ui.Alert({
     message: "No controller or no form found!" + form.id + ":\n" + form.controller ,
     alertType: constants.ALERT_TYPE_INFO
     }, {});
   }
} catch (e) {
    kony.ui.Alert({
        message: "ERR while listing functions: " + e,
        alertType: constants.ALERT_TYPE_INFO
    }, {});
}
